import React, { useState, useContext } from "react";
import "./App.css";
import { ThemeContext } from "./context";

const Row = props => (
  <div className="row">
    <span className="row-title">{props.label}</span>
    <span className="row-content">{props.children}</span>
  </div>
);

function Greeting(score) {
  const name = useFormInput("Player1");
  const theme = useContext(ThemeContext);

  return (
    <section className={theme}>
      <Row label={name.value}>
        <input {...name} />
      </Row>
      <Row label="{name} Score">{score} /></Row>
    </section>
  );
}

function useFormInput(initialValue) {
  const [value, setValue] = useState(initialValue);

  function handleChange(e) {
    setValue(e.target.value);
  }
  console.log({ value, onChange: handleChange });
  return {
    value,
    onChange: handleChange
  };
}

function Square({ value, onClick }) {
  return (
    <button className="square" onClick={onClick}>
      {value}
    </button>
  );
}

function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [OisNext, setOisNext] = useState(true);
  const [winner, setWinner] = useState(null);

  function renderSquare(i) {
    return (
      <Square
        value={squares[i]}
        onClick={() => {
          const nextSquares = squares.slice();
          nextSquares[i] = OisNext ? "X" : "O";
          setSquares(nextSquares);
          setOisNext(!OisNext);
          console.log(squares);
        }}
      />
    );
  }
  function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (
        squares[a] &&
        squares[a] === squares[b] &&
        squares[a] === squares[c]
      ) {
        return squares[a];
      }
    }
    return null;
  }
  return (
    <div>
      <div className="status" />
      <div className="board-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="board-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="board-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
    </div>
  );
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>Player 1</div>
          <ol />
        </div>
      </div>
    );
  }
}

export default Game;
