import React, { useState, useContext, useEffect } from "react";
import "./App.css";

const string =
  "À l'âge de 18 ans, Kurt rejoint son frère Rudolf à l'université de Vienne. Il a à ce moment déjà acquis un niveau universitaire en mathématiques et en philosophie. Bien qu'initialement inscrit pour étudier la physique théorique, il suit aussi un enseignement en mathématiques, avec le professeur Philipp Furtwängler et en philosophie. C'est à cette époque qu'il adhère au réalisme mathématique. Il lit Metaphysische Anfangsgründe der Naturwissenschaft (Premiers principes métaphysiques de la science de la nature) de Kant. À partir de 19255, il participe occasionnellement aux réunions hebdomadaires du Cercle de Viennen 1 où officient Moritz Schlick, Hans Hahn et Rudolf Carnap. Kurt étudie par la suite la théorie des nombres mais se tourne vite vers la logique mathématique après un séminaire donné par Moritz Schlick sur l’Introduction à la philosophie mathématique, de Bertrand Russell. C'est encore à l'université de Vienne qu'il rencontre celle qui deviendra (tardivement) sa femme, Adele Nimbursky (née Porkert). Il publie ses premiers articles sur la logique et assiste à une conférence de David Hilbert à Bologne sur la complétude et la cohérence des systèmes mathématiques. En 1929, Gödel devient citoyen autrichien (il était devenu automatiquement Tchécoslovaque à 12 ans lors du démembrement de l'Autriche-Hongrie de 1918), avant d'obtenir cette même année son doctorat, sous l'égide de Hans Hahn. Dans sa thèse, il établit la complétude du calcul des prédicats du premier ordre, résultat connu sous le nom de théorème de complétude de Gödel.";

function findTheWord(str) {
  var wordCounts = {};
  var words = str.match(/\S+\s*/g);

  for (var i = 0; i < words.length; i++)
    wordCounts["_" + words[i]] = (wordCounts["_" + words[i]] || 0) + 1;

  console.log(wordCounts);
}
findTheWord(string);
function Square({ value, onClick }) {
  return (
    <button className="square" onClick={onClick}>
      {value}
    </button>
  );
}

function Board(props) {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [OisNext, setOisNext] = useState(true);
  const [winner, setWinner] = useState(null);
  const [scores, setScores] = useState([0, 0]);

  function renderSquare(i) {
    return (
      <Square
        value={squares[i]}
        onClick={() => {
          if (winner === null) {
            const nextSquares = squares.slice();
            if (nextSquares[i] === null) {
              nextSquares[i] = OisNext ? "X" : "O";
            } else {
              return;
            }
            setSquares(nextSquares);
            setOisNext(!OisNext);
            console.log(squares);
          } else {
            return;
          }
        }}
      />
    );
  }
  function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (
        squares[a] &&
        squares[a] === squares[b] &&
        squares[a] === squares[c]
      ) {
        console.log("WINNER!");
        setWinner(squares[a]);
      }
    }
    console.log("NO WINNER");
    return;
  }

  function declareTurn() {
    return OisNext ? <p>Your turn X</p> : <p>Your turn O</p>;
  }
  useEffect(() => {
    calculateWinner(squares);
    if (winner !== null) {
      if (props.history.length === 0) {
        props.setHistory(squares);
      } else {
        props.setHistory(props.history + squares);
      }

      console.log("HISTORY" + props.history.length + props.history[0]);
      let nextScores = scores.slice();
      winner === "X" ? (nextScores[0] += 1) : (nextScores[1] += 1);
      setScores(nextScores);
      setWinner(null);
      setSquares(Array(9).fill(null));
    } else if (squares.includes(null) === false) {
      setSquares(Array(9).fill(null));
      return;
    }
  });

  return (
    <div className="main-container">
      <div className="game-container">
        <div className="scores">
          <h1>Scores</h1>
          <p>
            X : {scores[0]} - O : {scores[1]}
          </p>
        </div>
        <div className="status">{declareTurn()} </div>
        <div className="board-row">
          {renderSquare(0)}
          {renderSquare(1)}
          {renderSquare(2)}
        </div>
        <div className="board-row">
          {renderSquare(3)}
          {renderSquare(4)}
          {renderSquare(5)}
        </div>
        <div className="board-row">
          {renderSquare(6)}
          {renderSquare(7)}
          {renderSquare(8)}
        </div>
      </div>
    </div>
  );
}

function Game() {
  const [history, setHistory] = useState([]);
  return (
    <div className="game">
      <div className="game-board">
        <Board history={history} setHistory={setHistory} />
      </div>
      <div className="game-info">
        <div />
        <ol />
      </div>
    </div>
  );
}

export default Game;
