import React, { useState, useContext, useEffect } from "react";
import "./App.css";
import { ThemeContext } from "./context";

function Square({ value, onClick }) {
  return (
    <button className="square" onClick={onClick}>
      {value}
    </button>
  );
}

function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [OisNext, setOisNext] = useState(true);
  const [winner, setWinner] = useState(calculateWinner(squares));
  const [scores, setScores] = useState([0, 0]);

  function renderSquare(i) {
    return (
      <Square
        value={squares[i]}
        onClick={() => {
          if (winner === null) {
            const nextSquares = squares.slice();
            if (nextSquares[i] === null) {
              nextSquares[i] = OisNext ? "X" : "O";
            } else {
              return;
            }
            setSquares(nextSquares);
            setOisNext(!OisNext);
            console.log(squares);
          } else {
            return;
          }
        }}
      />
    );
  }
  function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (
        squares[a] &&
        squares[a] === squares[b] &&
        squares[a] === squares[c]
      ) {
        console.log("WINNER!");
        return squares[a];
      }
    }
    console.log("NO WINNER");
    return null;
  }

  function declareWinner() {
    if (winner !== null) {
      return <p>Congratulations {winner} !</p>;
    } else {
      return OisNext ? <p>Your turn X</p> : <p>Your turn O</p>;
    }
  }
  useEffect(() => {
    if (winner !== null) {
      let nextScores = scores.slice();
      winner === "X" ? (nextScores[0] += 1) : (nextScores[1] += 1);
      setScores(nextScores);
      setWinner(null);
      setSquares(Array(9).fill(null));
      setOisNext(!OisNext);
    } else {
      return;
    }
  });
  return (
    <div>
      <div className="status">{declareWinner({ winner })} </div>
      <div className="scores">
        X : {scores[0]} - O : {scores[1]}
      </div>
      <div className="board-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="board-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="board-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
    </div>
  );
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div />
          <ol />
        </div>
      </div>
    );
  }
}

export default Game;
