import React, { useState, useContext, useEffect } from "react";
import "./App.css";

function Game() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [historySquares, setHistorySquares] = useState(Array(9).fill(null));
  const [OisNext, setOisNext] = useState(true);
  const [winner, setWinner] = useState(null);
  const [scores, setScores] = useState([0, 0]);
  const [history, setHistory] = useState([]);

  function renderSquare(i) {
    return (
      <Square
        value={squares[i]}
        onClick={() => {
          if (winner === null) {
            const nextSquares = squares.slice();
            if (nextSquares[i] === null) {
              nextSquares[i] = OisNext ? "X" : "O";
            } else {
              return;
            }
            setSquares(nextSquares);
            setOisNext(!OisNext);
            console.log(squares);
          } else {
            return;
          }
        }}
      />
    );
  }

  function renderHistorySquare(i) {
    return <Square value={squares[i]} />;
  }

  useEffect(() => {
    function calculateWinner(squares) {
      const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
      ];
      for (let i = 0; i < lines.length; i++) {
        const [a, b, c] = lines[i];
        if (
          squares[a] &&
          squares[a] === squares[b] &&
          squares[a] === squares[c]
        ) {
          console.log("WINNER!");
          setWinner(squares[a]);
        }
      }
      console.log("NO WINNER");
      return;
    }
    calculateWinner(squares);
    if (winner !== null) {
      if (history.length === 0) {
        setHistory(squares);
      } else {
        setHistory(history + squares);
      }

      console.log("HISTORY" + props.history.length + history[0]);
      let nextScores = scores.slice();
      winner === "X" ? (nextScores[0] += 1) : (nextScores[1] += 1);
      setScores(nextScores);
      setWinner(null);
      setSquares(Array(9).fill(null));
    } else if (squares.includes(null) === false) {
      setSquares(Array(9).fill(null));
      return;
    }
  });

  return (
    <div className="game">
      <div className="game-board">
        <Board
          squares={squares}
          setSquares={setSquares}
          historySquares={historySquares}
          setHistorySquares={setHistorySquares}
          OisNext={OisNext}
          setOisNext={setOisNext}
          winner={winner}
          setWinner={setWinner}
          scores={scores}
          setScores={setScores}
          history={history}
          setHistory={setHistory}
          renderSquare={renderSquare}
        />
      </div>
      <div className="history-board">
        <HistoryBoard
          historySquares={historySquares}
          winner={winner}
          history={history}
          renderHistorySquare={renderHistorySquare}
        />
      </div>
      <div className="game-info">
        <div />
        <ol />
      </div>
    </div>
  );
}
async function HistoryBoard(props) {
  if (props.history.length === 0) {
    return <div>History</div>;
  }
  props.history.map(squares => {
    return (
      <div>
        <div className="board-row">
          {props.renderHistorySquare(0)}
          {props.renderHistorySquare(1)}
          {props.renderHistorySquare(2)}
        </div>
        <div className="board-row">
          {props.renderHistorySquare(3)}
          {props.renderHistorySquare(4)}
          {props.renderHistorySquare(5)}
        </div>
        <div className="board-row">
          {props.renderHistorySquare(6)}
          {props.renderHistorySquare(7)}
          {props.renderHistorySquare(8)}
        </div>
      </div>
    );
  });
}

function Square({ value, onClick }) {
  return (
    <button className="square" onClick={onClick}>
      {value}
    </button>
  );
}

function Board(props) {
  function declareTurn() {
    return props.OisNext ? <p>Your turn X</p> : <p>Your turn O</p>;
  }

  return (
    <div className="main-container">
      <div className="game-container">
        <div className="scores">
          <h1>Scores</h1>
          <p>
            X : {props.scores[0]} - O : {props.scores[1]}
          </p>
        </div>
        <div className="status">{declareTurn()} </div>
        <div className="board-row">
          {props.renderSquare(0)}
          {props.renderSquare(1)}
          {props.renderSquare(2)}
        </div>
        <div className="board-row">
          {props.renderSquare(3)}
          {props.renderSquare(4)}
          {props.renderSquare(5)}
        </div>
        <div className="board-row">
          {props.renderSquare(6)}
          {props.renderSquare(7)}
          {props.renderSquare(8)}
        </div>
      </div>
    </div>
  );
}

export default Game;

