import React, { useState, useContext } from "react";
import "./App.css";
import { ThemeContext } from "./context";

function Square({ value, onClick }) {
  return (
    <button className="square" onClick={onClick}>
      {value}
    </button>
  );
}

function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [OisNext, setOisNext] = useState(true);
  const winner = calculateWinner(squares);

  function renderSquare(i) {
    return (
      <Square
        value={squares[i]}
        onClick={() => {
          const nextSquares = squares.slice();
          nextSquares[i] = OisNext ? "X" : "O";
          setSquares(nextSquares);
          setOisNext(!OisNext);
          console.log(squares);
        }}
      />
    );
  }
  function calculateWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6]
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if (
        squares[a] &&
        squares[a] === squares[b] &&
        squares[a] === squares[c]
      ) {
        console.log("WINNER!");
        return squares[a];
      }
    }
    console.log("NO WINNER");
    return null;
  }
  function declareWinner({ winner, OisNext }) {
    if (winner !== null) {
      return <p>Congratulations {winner} !</p>;
    } else {
      OisNext ? <p>Your turn X</p> : <p>Your turn O</p>;
    }
  }
  return (
    <div>
      <div className="status">{declareWinner({ winner })} </div>
      <div className="board-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="board-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="board-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
    </div>
  );
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div />
          <ol />
        </div>
      </div>
    );
  }
}

export default Game;
