import React, { useState } from "react";
import "./App.css";

function Square({ value, onClick }) {
  return (
    <button className="square" onClick={onClick}>
      {value}
    </button>
  );
}

function Board() {
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [OisNext, setOisNext] = useState(true);
  const [winner, setWinner] = useState(null);

  function renderSquare(i) {
    return (
      <Square
        value={squares[i]}
        onClick={() => {
          const nextSquares = squares.slice();
          nextSquares[i] = OisNext ? "X" : "O";
          setSquares(nextSquares);
          setOisNext(!OisNext);
          console.log(squares);
        }}
      />
    );
  }
  function declareWinnner(squares) {
    //[0,1,2],[3,4,5],[6,7,8]
    for (let n = 0; n < 9; n + 3) {
      if (squares[n] == "X" && squares[n + 1] == "X" && squares[n + 2] == "X") {
        setWinner("X");
      } else if (
        squares[n] == "O" &&
        squares[n + 1] == "O" &&
        squares[n + 2] == "O"
      ) {
        setWinner("O");
      } else {
        //[0,3,6],[1,4,7],[2,5,8]
        for (let n = 0; n < 9; n++) {
          if (
            squares[n] == "X" &&
            squares[n + 3] == "X" &&
            squares[n + 6] == "X"
          ) {
            setWinner("X");
          } else if (
            squares[n] == "O" &&
            squares[n + 3] == "O" &&
            squares[n + 6] == "O"
          ) {
            setWinner("O");
          } else if (
            squares[0] == "X" &&
            squares[4] == "X" &&
            squares[8] == "X"
          ) {
            setWinner("X");
          } else if (
            squares[0] == "O" &&
            squares[4] == "O" &&
            squares[8] == "O"
          ) {
            setWinner("O");
          } else if (
            squares[2] == "O" &&
            squares[4] == "O" &&
            squares[6] == "O"
          ) {
            setWinner("O");
          } else if (
            squares[2] == "X" &&
            squares[4] == "X" &&
            squares[6] == "X"
          ) {
            setWinner("X");
          } else {
            return;
          }
        }
      }
    }
  }
  return (
    <div>
      <div className="status" />
      {declareWinnner()}
      <p>Winner : {winner}</p>
      <div className="board-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="board-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="board-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
    </div>
  );
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol />
        </div>
      </div>
    );
  }
}

export default Game;
