import React, { useState, useContext, useEffect } from "react";
import "./App.css";

function Game() {
  //players

  const [players, setPlayers] = useState({
    playerOne: "Player 1",
    playerTwo: "Player 2"
  });
  const [scores, setScores] = useState({ playerOne: 0, playerTwo: 0 });
  const [board, setBoard] = useState([
    Array(6).fill(null),
    Array(6).fill(null),
    Array(6).fill(null),
    Array(6).fill(null),
    Array(6).fill(null),
    Array(6).fill(null),
    Array(6).fill(null)
  ]);

  function handleColumnClick(id) {
    console.log(board[0]);
    console.log("The ID : " + id);
    let nextBoard = board.slice();
    let index = nextBoard[id].indexOf(null);
    if (~index) {
      nextBoard[id][index] = <div className="red-chip" />;
    }
    setBoard(nextBoard);

    return;
  }
  function handlePlayerChange(e) {
    e.preventDefault();
    let newPlayers = { ...players };
    e.target.name === "PlayerOne"
      ? (newPlayers = { ...players, playerOne: e.target.value })
      : (newPlayers = { ...players, playerTwo: e.target.value });
    console.log(newPlayers);
    setPlayers({ ...newPlayers });
  }

  return (
    <div>
      <div>
        <Players
          players={players}
          handlePlayerChange={handlePlayerChange}
          scores={scores}
        />
      </div>
      <div>
        <Board board={board} handleColumnClick={handleColumnClick} />
      </div>
    </div>
  );
}

// Board
function Board(props) {
  return (
    <div className="board board-style">
      <Column
        id={0}
        squares={props.board[0]}
        handleColumnClick={props.handleColumnClick}
      />
      <Column
        id={1}
        squares={props.board[1]}
        handleColumnClick={props.handleColumnClick}
      />
      <Column
        id={2}
        squares={props.board[2]}
        handleColumnClick={props.handleColumnClick}
      />
      <Column
        id={3}
        squares={props.board[3]}
        handleColumnClick={props.handleColumnClick}
      />
      <Column
        id={4}
        squares={props.board[4]}
        handleColumnClick={props.handleColumnClick}
      />
      <Column
        id={5}
        squares={props.board[5]}
        handleColumnClick={props.handleColumnClick}
      />
      <Column
        id={6}
        squares={props.board[6]}
        handleColumnClick={props.handleColumnClick}
      />
    </div>
  );
}
function Column(props) {
  return (
    <button
      className="column"
      onClick={() => {
        props.handleColumnClick(props.id);
      }}
    >
      {renderSquares(props.squares)}
    </button>
  );
}
function renderSquares(square) {
  return (
    <div className="squares">
      <div className="square shadow">{square[0]}</div>
      <div className="square shadow">{square[1]}</div>
      <div className="square shadow">{square[2]}</div>
      <div className="square shadow">{square[3]}</div>
      <div className="square shadow">{square[4]}</div>
      <div className="square shadow">{square[5]}</div>
    </div>
  );
}

//Players
function Players(props) {
  return (
    <div className="players">
      <input
        label="Player 1"
        type="text"
        name="PlayerOne"
        onChange={props.handlePlayerChange}
        value={props.players.playerOne}
      />
      <input
        label="Player 2"
        type="text"
        name="PlayerTwo"
        onChange={props.handlePlayerChange}
        value={props.players.playerTwo}
      />
      <p className="scores">
        {props.players.playerOne} VS {props.players.playerTwo}
      </p>
      <p className="scores">
        {props.scores.playerOne} - {props.scores.playerTwo}
      </p>
    </div>
  );
}

export default Game;

