import React, { useState } from "react";
import Row from "./Row";

export default function Greeting(props) {
  const [name, setName] = "";

  function handleNameChange(e) {
    setName(e.target.value);
  }
  return (
    <Row label="Name">
      <input value={name} onChange={handleNameChange} />
    </Row>
  );
}

