import React, { useState, useContext, useEffect } from "react";
import "./App.css";

function Game() {
  //players

  const [players, setPlayers] = useState({
    playerOne: "Player 1",
    playerTwo: "Player 2"
  });
  const [scores, setScores] = useState({ playerOne: 0, playerTwo: 0 });
  const [board, setBoard] = useState([
    Array(7).fill(null),
    Array(7).fill(null),
    Array(7).fill(null),
    Array(7).fill(null),
    Array(7).fill(null),
    Array(7).fill(null)
  ]);
  console.log(board);

  function handlePlayerChange(e) {
    e.preventDefault();
    let nextPlayers = { ...players };
    e.target.name === "PlayerOne"
      ? (nextPlayers = { ...players, playerOne: e.target.value })
      : (nextPlayers = { ...players, playerTwo: e.target.value });
    console.log(nextPlayers);
    setPlayers({ ...nextPlayers });
  }

  return (
    <div>
      <div>
        <Players
          players={players}
          handlePlayerChange={handlePlayerChange}
          scores={scores}
        />
      </div>
      <div>
        <Board board={board} />
      </div>
    </div>
  );
}

function Board(props) {
  return (
    <div className="board-container">
      <p>the Board</p>
      <div>{props.generateRow(props.board[0])}</div>
    </div>
  );
}

function Row(props) {
  return <div>ROW</div>;
}

//Players
function Players(props) {
  return (
    <div className="players-container">
      <input
        label="Player 1"
        type="text"
        name="PlayerOne"
        onChange={props.handlePlayerChange}
        value={props.players.playerOne}
      />
      <input
        label="Player 2"
        type="text"
        name="PlayerTwo"
        onChange={props.handlePlayerChange}
        value={props.players.playerTwo}
      />
      <p className="players-scores">
        {props.players.playerOne} VS {props.players.playerTwo}
      </p>
      <p className="players-scores">
        {props.scores.playerOne} - {props.scores.playerTwo}
      </p>
    </div>
  );
}

export default Game;

